export function $(t,e=document){return e.querySelector(t)}export function $$(t,e=document){return Array.from(e.querySelectorAll(t))}export function removeChildren(t){for(;t.lastChild;)t.removeChild(t.lastChild)}export function sum(t=0,e=0){return Number(t)+Number(e)}export function prod(t=1,e=1){return t*e}export function clamp(t,e,n){return Math.max(e,Math.min(n,t))}export function wrap(t,e,n){return(t+n)%(n-e)+e}export function*range(t,e,n=1){if(n=Math.abs(n),t<e)for(let r=t;r<=e;r+=n)yield r;else if(t>e)for(let r=t;r>=e;r-=n)yield r}export function withinRange(t,e,n,r=0){return r=Math.abs(r),t>=e&&t<=n&&(!r||Number.isInteger((t-e)/r))}export function unique(t,e,n){return e===n.indexOf(t)}export function isByte(t){return withinRange(t,0,255,1)}export function*digits(t,e=10){yield*t.toString(e)}export function*zip(...t){const e=Math.min(...t.map(t=>t.length));for(let n=0;n<e;n++){let e=[];for(const r of t)e.push(r[n]);yield e}}let t=null;export function debounce(e,n=100){t||(t=setTimeout(()=>{e(),t=null},n))}