export function $(t,n=document){return n.querySelector(t)}export function $$(t,n=document){return Array.from(n.querySelectorAll(t))}export function removeChildren(t){for(;t.lastChild;)t.removeChild(t.lastChild)}export function sum(t=0,n=0){return Number(t)+Number(n)}export function prod(t=1,n=1){return t*n}export function clamp(t,n,e){return Math.max(n,Math.min(e,t))}export function wrap(t,n,e){return e===n?n:(t+e)%(e-n)+n}export function*range(t,n,e=1){if(e=Math.abs(e),t<n)for(let r=t;r<=n;r+=e)yield r;else if(t>n)for(let r=t;r>=n;r-=e)yield r}export function withinRange(t,n,e,r=0){return r=Math.abs(r),t>=n&&t<=e&&(0===r||Number.isInteger((t-n)/r))}export function unique(t,n,e){return n===e.indexOf(t)}export function isByte(t){return withinRange(t,0,255,1)}export function*digits(t,n=10){yield*t.toString(n)}export function*zip(...t){const n=Math.min(...t.map(t=>t.length));for(let e=0;e<n;e++){let n=[];for(const r of t)n.push(r[e]);yield n}}let t=null;export function debounce(n,e=100){t||(t=setTimeout(()=>{n(),t=null},e))}export function randInt(t,n){return Math.round(Math.random()*(n-t))+t}export function memoize(t){const n={};return function(e){return n.hasOwnProperty(e)||(n[e]=t(e)),n[e]}}